var theToggle=document.getElementById("toggle");function hasClass(e,s){return new RegExp(" "+s+" ").test(" "+e.className+" ")}function addClass(e,s){hasClass(e,s)||(e.className+=" "+s)}function removeClass(e,s){var a=" "+e.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(e,s)){for(;0<=a.indexOf(" "+s+" ");)a=a.replace(" "+s+" "," ");e.className=a.replace(/^\s+|\s+$/g,"")}}function toggleClass(e,s){var a=" "+e.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(e,s)){for(;0<=a.indexOf(" "+s+" ");)a=a.replace(" "+s+" "," ");e.className=a.replace(/^\s+|\s+$/g,"")}else e.className+=" "+s}theToggle.onclick=function(){return toggleClass(this,"on"),!1};